import { Component } from '@angular/core';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent {
  users: any[] = [];
  constructor(private userService: UserService) {}
  ngOnInit() {
    this.userService.getUsers().subscribe(users => {
      this.users= users[0];
      console.log("Users : ",this,users[0][0] )
    });
  }
  editUser(user: any) {
    // Implement edit logic here
    console.log('Editing user:', user);
  }

  deleteUser(userId: number) {
    // Implement delete logic here
    this.deleteUser(userId)
    console.log('Deleting user with ID:', userId);
  }
}
<div class="d-flex">
    <div *ngFor="let user of users" class="card-container">
        <div class="card mx-2" style="width: 18rem;">
            <img [src]="user.profilePictureUrl" class="mx-auto card-img-top w-50">
            <div class="card-body">
                <h6>User Name : {{ user.name }}</h6>
                <p class="card-text">Email: {{ user.email }}</p>
                <button class="btn btn-primary" (click)="editUser(user)">Edit</button>
                <button class="btn btn-danger" (click)="deleteUser(user.id)">Delete</button>
            </div>
        </div>
    </div>
</div>
