import { Component, Input, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { ToastService } from '../service/toast.service';
import { Renderer2, ElementRef } from '@angular/core';

@Component({
  selector: 'app-edit-user-modal',
  templateUrl: './edit-user-modal.component.html',
  styleUrls: ['./edit-user-modal.component.css']
})
export class EditUserModalComponent implements OnInit {
  userId: any = localStorage.getItem('selectedUser');
  user: any = {
    name: '',
    email: '',
    profilePictureUrl: ''
  };

  constructor(private toast: ToastService, private userService: UserService, private renderer: Renderer2, private el: ElementRef) { }

  ngOnInit() {
    // Fetch user data by ID when the component initializes
    this.userService.getUserById(this.userId).subscribe(user => {
      this.user = user[0];
      console.log("Edit Modal : ", this.user[0])
    });
  }

  updateUser() {
    this.userService.updateUser(this.user).subscribe(
      (response: any) => {
        this.toast.showToast('User updated successfully!', 'Success', 'success');
        this.closeModal();
      },
      (error: any) => {
        this.toast.showToast('Failed to update user. Please try again.', 'Alert', 'warning');
      }
    );
  }
  closeModal() {
    const modalElement = this.el.nativeElement.querySelector('#editUserModal');
    this.renderer.removeClass(modalElement, 'show');
    this.renderer.setStyle(modalElement, 'display', 'none');
    this.renderer.removeClass(document.body, 'modal-open');
    const modalBackdrop = document.querySelector('.modal-backdrop');
    if (modalBackdrop) {
      modalBackdrop.remove();
    }
  }

}
<p>edit-user-modal works!</p>
<div class="modal fade" id="editUserModal" tabindex="-1" aria-labelledby="editUserModalLabel" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title" id="editUserModalLabel">Edit User</h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
                <form>
                    <div class="mb-3">
                        <label for="editName" class="form-label">Username</label>
                        <input type="text" class="form-control" id="editName" [(ngModel)]="user.name" name="name">
                    </div>
                    <div class="mb-3">
                        <label for="editEmail" class="form-label">Email</label>
                        <input type="email" class="form-control" id="editEmail" [(ngModel)]="user.email" name="email">
                    </div>
                    <div class="mb-3">
                        <label for="editImage" class="form-label">Image URL</label>
                        <input type="text" class="form-control" id="editImage" [(ngModel)]="user.profilePictureUrl"
                            name="profilePictureUrl">
                    </div>
                </form>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary" (click)="updateUser()">Save changes</button>
            </div>
        </div>
    </div>
</div>
