import { Component, OnInit } from '@angular/core';

import { FormBuilder, FormGroup, FormsModule }   from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms';
@Component({
  selector: 'app-contactus',
  templateUrl: './contactus.component.html',
  styleUrls: ['./contactus.component.css']
})
export class ContactusComponent implements OnInit {
myform!: FormGroup;
  constructor(private toast: ToastrService,private fb: FormBuilder){}
  ngOnInit(): void {
this.myform = this.fb.group({
  email: '',
  password:''
});

  }

  OnSubmit()
  {
    if(this.myform.controls['email'].value =="") 
    {
      document.getElementById("exampleInputEmail1")?.focus();
      this.toast.warning('Please enter email!', 'Alert',{
        timeOut:2000,
        progressBar: true,
        closeButton:true,
        tapToDismiss:true,
        newestOnTop:true
      });
    }
    else if(this.myform.controls['password'].value =="") 
    {
      document.getElementById("exampleInputPassword1")?.focus();

      document.getElementById('password')?.focus();
    }
    else 
    {
      this.toast.success('Form Submitted Successfully', 'Success',{
        timeOut:2000,
        progressBar: true,
        closeButton:true,
        tapToDismiss:true,
        newestOnTop:true
      });
    }

  }

}
