import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ToastService {

  constructor() { }


  showToast(message:string,title:string)
  {
    this.toast.warning('Please enter password!', 'Alert',{
      timeOut:2000,
      progressBar: true,
      closeButton:true,
      tapToDismiss:true,
      newestOnTop:true
    });
  }
}
